package com.nullspace.server;import io.netty.channel.Channel;public abstract class NSServer {	protected String mIP = null;	protected int mPort = -1;	protected Thread mServerThread = null;	protected Channel mServerChannel;		public NSServer(String ip, int port)	{		mIP = ip;		mPort = port;		mServerThread = new Thread(new Runnable() {						@Override			public void run() {				Run();			}		});	}	protected abstract void Run();		public void Start()	{		mServerThread.setDaemon(true);		mServerThread.start();		System.out.println(toString());	}		public void Stop()	{		if (mServerChannel != null)		{			mServerChannel.close();			try 			{				if (mServerThread != null)					mServerThread.join();			} 			catch (InterruptedException e) 			{				e.printStackTrace();			}		}	}}