package com.nullspace.utils;import java.io.IOException;import com.fasterxml.jackson.core.JsonParseException;import com.fasterxml.jackson.core.JsonProcessingException;import com.fasterxml.jackson.databind.JsonMappingException;import com.fasterxml.jackson.databind.ObjectMapper;public class NSJsonUtils{	public static <T> String ToJson(T object) throws JsonProcessingException	{		ObjectMapper mapper = new ObjectMapper();		return mapper.writeValueAsString(object);	}	public  static <T> T FromJson(String json, Class<T> clazz) throws JsonParseException, JsonMappingException, IOException 	{		ObjectMapper mapper = new ObjectMapper();		return mapper.readValue(json, clazz);	}	public static class TestJsonObject	{		public String name;		public int age;		public  TestJsonObject() 		{					}	}		public static void main(String[] argvs) throws IOException	{		TestJsonObject testJsonObject = new TestJsonObject();		testJsonObject.name = "yangkai";		testJsonObject.age = 28;		String jsonString = NSJsonUtils.ToJson(testJsonObject);		System.err.println(jsonString);		TestJsonObject jsonObject = NSJsonUtils.FromJson(jsonString, TestJsonObject.class);			System.err.println(jsonObject.toString());	}}